import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthModal = ({ setShowModal, isSignUp }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Check for user details in localStorage
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (storedUser && storedUser.userId && storedUser.token) {
            // Redirect the user to the appropriate location
            if (isSignUp) {
                navigate('/onboarding');
            } else {
                navigate('/dashboard');
            }
        }
    }, [navigate, isSignUp]);

    const handleClick = () => {
        setShowModal(false);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            if (isSignUp && password !== confirmPassword) {
                setError('Passwords need to match!');
                return;
            }

            const response = await axios.post(
                `http://localhost:8000/${isSignUp ? 'signup' : 'login'}`,
                { email, password }
            );

            const { token, userId } = response.data;
            // Store user details in localStorage
            localStorage.setItem('user', JSON.stringify({ userId, token }));

            if (isSignUp) {
                navigate('/onboarding');
            } else {
                navigate('/dashboard');
            }
        } catch (error) {
            setError(error.response?.data?.error || 'An error occurred');
        }
    };

    return (
        <div className="auth-modal">
            <div className="close-icon" onClick={handleClick}>
                â“§
            </div>

            <h2>{isSignUp ? 'CREATE ACCOUNT' : 'LOG IN'}</h2>
            <p>
                By clicking Log In, you agree to our terms. Learn how we process your data in our Privacy Policy and
                Cookie Policy.
            </p>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    id="email"
                    name="email"
                    placeholder="email"
                    required={true}
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    id="password"
                    name="password"
                    placeholder="password"
                    required={true}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {isSignUp && (
                    <input
                        type="password"
                        id="password-check"
                        name="password-check"
                        placeholder="confirm password"
                        required={true}
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                    />
                )}
                <input className="secondary-button" type="submit" />
                <p style={{ color: 'red', fontStyle: 'italic' }}>{error}</p>
            </form>

            <hr />
            <h2>GET THE APP</h2>
        </div>
    );
};

export default AuthModal;
